INSERT INTO `tblberita` (`idBerita`, `tglBerita`, `halaman`, `judulBerita`, `rubrikBerita`, `kota`, `isiBerita`, `penulisBerita`, `editor`, `layouter`, `statusBerita`, `createdAt`, `aktif`) VALUES
(95, '2013-10-25', '5', 'Bahasa Asli', '3', 'Jakarta', '&lt;p&gt;Indonesia memiliki sekitar 700 bahasa asli , namun yang terekam dan terlestarikan dalam bentuk tulisan hanya ada 13 bahasa. &amp;ldquo;Hingga kini baru naskah berbahasa Jawa, Jawa Kuna, dan Bugis yang diusulkan ke UNESCO sebagai dokumen Memory of the World (MOW), padahal dokumen berbahasa Nusantara lain juga sangat potensial,&amp;rdquo; ujar Dr. Ir. Fatimah Zulfah S. Padmadinata, Deputi Bidang Jasa Ilmiah LIPI sekaligus sebagai Ketua Komite Nasional MOW Indonesia di Pusat Data dan Informasi Ilmiah LIPI, Kampus LIPI Gatot Subroto, Jakarta, pada Jum&amp;rsquo;at (18/10) lalu.&lt;/p&gt;\r\n&lt;p&gt;Fatimah menjelaskan, saat ini dokumen-dokumen yang diusulkan untuk MOW antara lain adalah Prasasti Sriwijaya yang berbahasa Melayu Kuna, Hikayat Po&amp;rsquo;cut Mohamad dalam bahasa Aceh, serta Sanghyang Siksakandang Karesian dan Sewaka Darma yang berbahasa Sunda Kuna.&lt;/p&gt;\r\n&lt;p&gt;MOW sendiri merupakan ingatan kolektif manusia berupa warisan atau pusaka dokumenter yang secara sah dapat menjadi bukti kejadian penting dalam sejarah umat manusia. MoW digagas oleh UNESCO pada tahun 1992 dan mempunyai tujuan memfasilitasi preservasi warisan/pusaka dokumenter melalui berbagai cara yang sesuai dengan perkembangan teknologi, meningkatkan kesadaran akan pengamanan dan keberadaan warisan/pusaka dokumenter baik secara individu maupun secara kolektif, dan membantu akses kepada masyarakat baik nasional maupun internasional kepada warisan/pusaka dokumenter bagi pengembangan ilmu pengetahuan di masa yang akan datang&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;Perlu ditunjuk seorang yang ahli dalam bidangnya untuk mengangkat dan kemudian menggarap dokumen tersebut untuk pengusulan sebagai MOW,&amp;rdquo; saran Fatimah.&lt;/p&gt;', '12', NULL, '0', 0, '2013-10-24 13:07:00', '1'),
(96, '2013-10-25', '5', 'basa', '3', 'jakarta', '&lt;p&gt;Indonesia memiliki sekitar 700 bahasa asli , namun yang terekam dan terlestarikan dalam bentuk tulisan hanya ada 13 bahasa. &amp;ldquo;Hingga kini baru naskah berbahasa Jawa, Jawa Kuna, dan Bugis yang diusulkan ke UNESCO sebagai dokumen Memory of the World (MOW), padahal dokumen berbahasa Nusantara lain juga sangat potensial,&amp;rdquo; ujar Dr. Ir. Fatimah Zulfah S. Padmadinata, Deputi Bidang Jasa Ilmiah LIPI sekaligus sebagai Ketua Komite Nasional MOW Indonesia di Pusat Data dan Informasi Ilmiah LIPI, Kampus LIPI Gatot Subroto, Jakarta, pada Jum&amp;rsquo;at (18/10) lalu.&lt;/p&gt;\r\n&lt;p&gt;Fatimah menjelaskan, saat ini dokumen-dokumen yang diusulkan untuk MOW antara lain adalah Prasasti Sriwijaya yang berbahasa Melayu Kuna, Hikayat Po&amp;rsquo;cut Mohamad dalam bahasa Aceh, serta Sanghyang Siksakandang Karesian dan Sewaka Darma yang berbahasa Sunda Kuna.&lt;/p&gt;\r\n&lt;p&gt;MOW sendiri merupakan ingatan kolektif manusia berupa warisan atau pusaka dokumenter yang secara sah dapat menjadi bukti kejadian penting dalam sejarah umat manusia. MoW digagas oleh UNESCO pada tahun 1992 dan mempunyai tujuan memfasilitasi preservasi warisan/pusaka dokumenter melalui berbagai cara yang sesuai dengan perkembangan teknologi, meningkatkan kesadaran akan pengamanan dan keberadaan warisan/pusaka dokumenter baik secara individu maupun secara kolektif, dan membantu akses kepada masyarakat baik nasional maupun internasional kepada warisan/pusaka dokumenter bagi pengembangan ilmu pengetahuan di masa yang akan datang&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;Perlu ditunjuk seorang yang ahli dalam bidangnya untuk mengangkat dan kemudian menggarap dokumen tersebut untuk pengusulan sebagai MOW,&amp;rdquo; saran Fatimah.&lt;/p&gt;', '12', NULL, '0', 0, '2013-10-24 13:07:25', '1'),
(97, '2013-10-27', '5', 'Selamat datang di unikom', '5', 'Bandung', '&lt;p&gt;&lt;span&gt;UNIKOM Kuliah Online merupakan salah satu sarana pembelajaran interaktif, dimana para dosen dan para mahasiswa dapat berkomunikasi dengan menggunakan media internet.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Para dosen dapat memberikan materi, baik berupa file, video, tulisan ataupun materi-materi kuliah dalam bentuk dokumen lainnya, memberikan informasi-informasi penting kepada para mahasiswanya, membaca dan memberikan jawaban untuk pertanyaan-pertanyaan yang diberikan para mahasiswanya, dan memasukan soal-soal ujian juga menentukan waktu berlakunya ujian untuk para mahasiswanya.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Sedangkan untuk para mahasiswa, dapat memilih kelas, dan mata kuliah yang akan diikuti, mahasiswa bisa mendapatkan materi perkuliahan berupa file atau bacaan dari dosen yang bersangkutan, mengirimkan pertanyaan kepada dosen mata kuliah tersebut, mengirimkan kontak pada mahasiswa lain, melihat informasi dari dosen yang bersangkutan, dan melakukan ujian pada waktu yang telah ditetapkan. Mahasiswa juga dapat melihat hasil prestasinya pada modul raport.&lt;/span&gt;&lt;/p&gt;', '1', NULL, '0', 1, '2013-10-26 03:53:33', '1'),
(98, '2013-10-28', '5', 'Ini Daftar Aset Milik Adiguna yang Dirusak oleh Floren ', '2', 'Jakarta', '&lt;p&gt;Polisi memastikan pelaku perusakan terhadap kediaman Adiguna Sutowo di Pulomas, Jakarta Timur, adalah seorang wanita bernama Floren. Ada barang-barang dan aset milik Adiguna yang rusak. Ini daftarnya.&lt;/p&gt;\r\n&lt;p&gt;Berdasarkan informasi dari Kabid Humas Polda Metro Jaya, Kombes Pol Rikwanto, Floren datang bersama sopir Adiguna, Dariyono. Saat sang sopir membuka pagar, wanita yang digambarkan muda dan cantik ini langsung mengambil alih setir dan merusak semua yang ada di depan rumah.&lt;/p&gt;\r\n&lt;p&gt;&quot;Satpam melihat sopir disuruh turun dan pelaku mengambil alih kemudi dan memundurkan mobil sedan tersebut menabrak Toyota Alphard mengenai pintu sebelah kiri belakang,&quot; jelas Rikwanto dalam pesan singkat kepada detikcom, Minggu (27/10/2013).&lt;/p&gt;\r\n&lt;p&gt;Kemudian, Floren menancap gas dan menabrak pintu gerbang dan pintu garasi yang di dalamnya ada mobil Mercy bernopol B 171 VAS. Saking kencangnya, Floren juga menabrak pintu dapur hingga rusak.&lt;/p&gt;\r\n&lt;p&gt;Setelah itu, mobil Floren mundur dan merusak gerbang gudang. Karena mobilnya langsung mati, Floren langsung masuk ke garasi dan membanting TV 21 inchi.&lt;/p&gt;\r\n&lt;p&gt;&quot;Pelaku mengambil payung dari mobil Mercy korban dan oleh pelaku payung tersebut digunakan untuk memukul mobil LEXUS B 171 AV,&quot; sambungnya.&lt;/p&gt;\r\n&lt;p&gt;Dengan demikian, aset yang rusak di rumah Adiguna adalah:&lt;/p&gt;\r\n&lt;ol&gt;\r\n&lt;li&gt;Pagar rumah&lt;/li&gt;\r\n&lt;li&gt;Mobil Mercy&lt;/li&gt;\r\n&lt;li&gt;Mobil Lexus&lt;/li&gt;\r\n&lt;li&gt;Mobil Alphard&lt;/li&gt;\r\n&lt;li&gt;Pintu gudang&lt;/li&gt;\r\n&lt;li&gt;TV 21 inchi&lt;/li&gt;\r\n&lt;/ol&gt;\r\n&lt;p&gt;&lt;br /&gt;Pengacara istri Adiguna sebelumnya menyebut total kerugian diprediksi sekitar ratusan juta rupiah.&lt;/p&gt;', '1', '1', '1', 3, '2013-10-27 12:27:54', '1'),
(99, '2013-10-28', '3', '523 Pelaku Olahraga Dapat Penghargaan Pemerintah', '5', 'Bandung', '&lt;p&gt;Floren menabrak pagar rumah dan merusak sejumlah properti milik Adiguna Sutowo di Pulogadung, Jakarta Timur. Tak hanya itu, dia juga teriak-teriak sambil mencari istri kedua Adiguna, Vika Dewayani.&lt;/p&gt;\r\n&lt;p&gt;Kabid Humas Polda Metro Jaya Kombes Pol Rikwanto mengatakan, Floren berteriak tak lama setelah menabrakkan mobil Mercy ke pagar. Dia mencari Vika, yang saat itu sedang berada di Bali.&lt;/p&gt;', '1', '1', '1', 3, '2013-10-27 12:17:26', '1'),
(100, '2013-10-28', '45', 'MAX-R, Limosin Canggih dari Bandung', '13', 'Bandung', '&lt;p&gt;&lt;span&gt;Rumah modifikasi asal Bandung yang terkenal sebagai langganan juara kontes modifikasi, Signal Kustom Built menelurkan lagi satu konsep mobil yang canggih.&lt;/span&gt;Bengkel modifikasi yang dimotori oleh Andre Mulyadi memperkenalkan sebuah mobil yang diberi nama MAX-R Concept.&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Signal Kustom Built memadukan sebuah karya seni tinggi dengan teknologi tinggi dalam sebuah super sport car dengan tampilan sedan limosin.&amp;nbsp;&lt;/span&gt;&lt;span&gt;Eksteriornya menggunakan teknik multi layer clear coating yang menghasilkan tampilan wet look, memadukan seni freehand pinstriping dengan seni realis grafic, dalam guratan 43 warna yang dapat bertransformasi dengan permainan warna hologram hingga &amp;ldquo;bunglon&amp;rdquo;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Untuk memerintahkan semua fitur di mobil ini menggunakan aplikasi layar sentuh LED, salah satu konsep yang belum pernah diaplikasikan di mobil konsep manapun.&lt;/span&gt;&lt;/p&gt;', '18', NULL, '1', 3, '2013-10-27 12:43:07', '1'),
(101, '2013-10-28', '3', 'Pemulangan Ika, Jamaah Haji yang Melahirkan di Pemondokan Makkah Ditunda', '4', 'jakarta', '&lt;p&gt;&lt;span&gt;Kadaker Makkah &lt;em&gt;Arsyad Hidayat kaget mendengar kabar ada jamaah haji asal Bogor, Ika binti Abdurrozak, melahirkan di pemondok&lt;/em&gt;an. Ia meminta pemulangan Ika dan bayi Makiah Marwah ditangguhkan.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;&quot;&lt;em&gt;Saya pukul 06.00 WAS ditelepon oleh Kasektor 6 Ali Zakiyuddin. Katanya, Pak Arsyad, sampean dapat cucu.&lt;/em&gt; &lt;span style=&quot;text-decoration: underline;&quot;&gt;Saya bilang, cucu darimana?&lt;/span&gt; Wong saya masih muda kok sudah dapat cucu. &lt;strong&gt;Kata Ali,&lt;/strong&gt; ini ada jamaah haji yang melahirkan. Saya kaget, kok bisa,&quot; cerita Arsyad Hidayat saat dihubungi Tim Media Center Haji (MCH), Minggu (27/10/2013).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Arsyad bersyukur Ika dan bayi mungilnya dalam kondisi sehat dan langsung ditangani tim kesehatan.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;&quot;Saya minta pagi itu juga untuk segera ditanazulkan karena informasinya, JKS/14 akan pulang pada malam harinya. Saya minta tolong ditanazulkan sampai jamaah yang bersangkutan bersama bayinya betul-betul layak untuk dipulangkan,&quot; kata Arsyad.&lt;/span&gt;&lt;/p&gt;', '1', '15', '1', 3, '2013-10-27 15:11:39', '1'),
(102, '2013-10-29', '15', 'teror kampanye india', '6', 'new delhi', '&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Television channels showed pictures of shattered windows of buses, smoke billowing and people running in panic.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;It was the first outbreak of violence since Modi, the challenger from the Hindu nationalist Bharatiya Janata Party, began an aggressive campaign to oust the ruling Congress-led coalition.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;To his critics Modi is a deeply polarising figure under whose watch Muslims were attacked in deadly rioting in 2002 in the western state of Gujarat which he has governed for three straight terms.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;But to his growing army of supporters including many in Indian business, the&amp;nbsp;BJP&amp;nbsp;strongman is the best hope the nation has of turning around the economy after helping turn Gujarat into one of the country''s fastest growing states.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Modi himself has denied any allegations of bias against Muslims and a Supreme Court-appointed panel cleared him of any wrongwrong in the riots.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;On Sunday, he sought to broaden his appeal, saying Hindus and Muslims both needed to be lifted out of poverty.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;&quot;Do you want to fight against poverty or against Hindus?,&quot; he asked. &quot;Poor Muslims have to fight against poverty. Poor Hindus have to fight against poverty.&quot;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Modi did not mention the blasts in his speech, but later called them &quot;deeply saddening and unfortunate&quot; on Twitter. He appealed for peace and calm.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Police inspector general Sushil M Khopde said five people were killed in the blasts. Three unexploded bombs were found at the venue of the rally.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;No one has claimed responsibility for the blasts. However, police say they have detained some people for questioning.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;Sunday''s massive rally was intended as a show of strength in Bihar where the governing party broke off a 17-year alliance with the&amp;nbsp;BJP&amp;nbsp;after it picked Modi as its candidate to lead the party.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;The state sends 40 lawmakers to the lower house of parliament and could prove decisive in a new government''s formation.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: small;&quot;&gt;The&amp;nbsp;BJP&amp;nbsp;has picked up support since elevating Modi as its candidate for prime minister last month, but would need allies to form a government, two new opinion polls showed earlier this month. (Reporting by Rajesh Kumar Singh; Editing by Sanjeev Miglani and Jeremy Laurence)&lt;/span&gt;&lt;/p&gt;', '15', NULL, '1', 3, '2013-10-28 17:02:36', '1'),
(103, '2013-10-29', '4', 'Honor Dokter Magang Menjadi Rp 2,5 Juta', '3', 'JAKARTA (HN) -', '&lt;p&gt;&lt;span style=&quot;font-size: 14pt;&quot;&gt;Ketua Pengurus Besar Ikatan Dokter Indonesia (IDI) Zaenal Abidin meminta kepada pemerintah untuk lebih memperhatikan kesejahteraan para dokter internship (magang). Biaya bantuan hidup alias honorarium saat ini sebesar Rp 1,2 juta per bulan terlalu rendah untuk kebutuhan hidup sehari-hari.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&quot;Saya tidak ingin dokter itu dibayar murah. Seharusnya honor dokter internship menyesuaikan biaya hidup. Sekarang ini serba mahal, apalagi BBM juga naik,&quot; kata Zaenal saat dihubungi &lt;em&gt;HARIAN NASIONAL&lt;/em&gt; di Jakarta, Senin (28/10). Ia mengatakan dokter adalah tenaga kesehatan strategis, yang tidak bisa digantikan oleh profesi lain.&lt;/p&gt;\r\n&lt;p&gt;Sebelumnya, Komunitas Dokter Indonesia Bersatu (DIB) menyoroti bantuan hidup yang tidak manusiawi bagi dokter magang. Selain dari segi honorarium kecil, dokter magang terkendala sarana dan prasarana di daerah. &quot;Padahal kami dituntut kerja profesional. Ada juga yang belum menerima honor selama 6 bulan,&quot; kata Juru bicara Komunitas DIB dr Agung Sapta Adi.&lt;/p&gt;\r\n&lt;p&gt;Wakil Menteri Kesehatan Ali Ghufron Mukti mengatakan, pemerintah akan tetap meneruskan pogram internship ke depan. Menurutnya, dokter-dokter yang menjalani menyatakan menerima manfaatnya baik untuk kemandirian atau pemahaman.&lt;/p&gt;\r\n&lt;p&gt;Ali menuturkan, pemerintah juga sudah berkomitmen akan menaikkan honorarium pada tahun anggaran 2014 menjadi Rp 2,5 juta per bulan. &quot;Dari Kemenkes seperti itu,&quot; kata Ali dalam pesan singkatnya, Senin.&lt;/p&gt;\r\n&lt;p&gt;Terkait dengan kenaikan tersebut, Zaenal mengharapkan, jumlah itu bisa mengurangi beban biaya hidup para dokter. &amp;ldquo;Jika memang dirasa masih terlalu murah, maka mohon kepada pemerintah pusat ataupun daerah bisa membantu,&quot; katanya.&lt;/p&gt;\r\n&lt;p&gt;Di sejumlah daerah, katanya, sudah ada pemerintah daerah (pemda) yang memberikan tambahan biaya bantuan. Ia mengusulkan, agar ke depan dokter magang bisa mendapatkan jasa medis dari pasien.&lt;/p&gt;\r\n&lt;p&gt;Oleh karena itu, ia meminta kepada pemda dan RSUD di daerah untuk memperlakukan dokter magang sebagai dokter, karena mereka sudah bukan lagi mahasiswa. &quot;Jika sudah mahir pemerintah daerah juga yang akan menggunakan kemahirannya,&quot; katanya.&lt;/p&gt;\r\n&lt;p&gt;Menyangkut program internship yang dikritik memberatkan para dokter muda, menurutnya, hal itu tidak benar. &quot;Enggak ada yang ribet,&quot; katanya.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Program internship dokter adalah program magang bagi dokter yang baru menyelesaikan masa pendidikan profesi. Program ini dibentuk sejak 2010 oleh Kementerian Kesehatan. Sebelum dokter muda mendapatkan surat tanda registrasi (STR) - sebagai syarat izin praktik, mereka wajib mengikuti program ini terlebih dulu . Lama peserta mengikuti program ini antara 12 bulan. Penempatan para dokter dilakukan oleh Komite Internship Dokter Indonesia (KIDI) yang berada di tingkat pusat dan provinsi. Para dokter juga menerima sejumlah tunjangan seperti akomodasi, fasilitas, tempat tinggal, dan honor per bulan.&lt;/p&gt;\r\n&lt;p&gt;Menurut dia, program ini awalnya digagas oleh IDI sebagai cara untuk memantapkan kualitas dan kemahiran dokter muda. Mengingat saat ini masa pendidikan di fakultas kedokteran lebih cepat dibandingkan sebelumnya, sehingga perlu peningkatan kualitas dokter. &quot;Sekarang lima tahun sudah bisa lulus, sebelumnya &lt;em&gt;kan&lt;/em&gt; sampai tujuh tahun,&quot; katanya.&lt;/p&gt;\r\n&lt;p&gt;Akan tetapi, katanya, IDI tak memiliki cukup dana untuk melaksanakan porgram itu. Akhirnya, IDI melimpahkan program internship ke Kementerian Kesehatan. Dari sinilah, kemudian pemerintah menentukan biaya hidup dokter-dokter internship yang magang selama setahun. Masa pelaksanaan magang yaitu delapan bulan di RSUD dan empat bulan di puskesmas, dengan harapan bisa beradaptasi dengan lingkungan kerja. Pogram ini sudah dicanangkan sejak 2010. Di hampir seluruh negara, kata Zaenal, menerapkan program serupa. Di Malaysia, misalnya, malah membuat aturan lama magang sampai dua tahun.&lt;/p&gt;\r\n&lt;p&gt;Ia mengatakan, profesi dokter adalah pilihan hidup dan tidak ada paksaan untuk menjadi dokter. Hal itulah, kata Zaenal, yang perlu dipahami bagi para calon mahasiswa kedokteran. &quot;Jangan sampai nanti harapannya tidak sama dengan yang terjadi,&quot; katanya.&lt;/p&gt;\r\n&lt;p&gt;Ia mengatakan, bisa saja dokter yang baru lulus tidak mengikuti program internship. Namun, mereka tidak bisa melakukan praktik secara mandiri. &quot;Bisa praktik tapi mengikuti dokter senior dan tidak bisa mengeluarkan resep atas namanya,&quot; katanya. Ia mengatakan, program ini dibuat, karena sebagai syarat dokter untuk bisa membuka praktik.&lt;/p&gt;\r\n&lt;p&gt;&quot;Mereka yang enggak mau internship bisa saja menunggu menjadi pegawai. Tapi &lt;em&gt;kan&lt;/em&gt; menganggur bisa setahun, kalau ikut program ini bisa sambil meningkatkan kemahirannya,&quot; katanya.&lt;/p&gt;', '12', '14', '1', 3, '2013-10-28 16:55:26', '1'),
(104, '2013-10-29', '4', 'Peliknya Menembus Gurun Australia', '3', 'Jakarta', '&lt;p&gt;&lt;span style=&quot;font-size: 14pt;&quot;&gt;KABAR gembira itu memberikan harapan baru bagi anak-anak Indonesia untuk berkiprah di mata internasional. Menjelang 28 Oktober &amp;ndash; Hari Sumpah Pemuda, rombongan mahasiswa Institut Teknologi Sepuluh Nopember Surabaya (ITS), Jawa Timur menorehkan nama dalam ajang World Solar Challenge 2013 di Australia pada 6-13 Oktober lalu. Ini adalah sebuah kompetisi balap mobil bertenaga surya internasional yang digelar dua tahunan sejak tahun 1987.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;Kami berhasil mencatat sejauh 748 kilometer (km) dari jarak Darwin-Adelaide sekitar 3.000 km,&amp;rdquo; kata Siti Choirun Nisa (22), salah satu anggota tim lomba saat dihubungi &lt;em&gt;HARIAN NASIONAL&lt;/em&gt; dari Jakarta, Sabtu (19/10). Perjalanan tersebut ditempuh selama tujuh hari.&lt;/p&gt;\r\n&lt;p&gt;Mereka butuh perjuangan keras untuk menembus rute, yang sebagian besar gurun dan tidak ada fasilitas lain, seperti listrik, air, dan saluran telepon. Lomba ini selevel dengan reli Paris-Dakar.&lt;/p&gt;\r\n&lt;p&gt;Tim yang berangkat sebanyak 18 orang dan satu dosen pembimbing. Mereka masuk dalam kelas &lt;em&gt;Challenger&lt;/em&gt; - level tertinggi dalam lomba &amp;ndash; dua level di bawahnya, kelas &lt;em&gt;cruiser&lt;/em&gt; dan &lt;em&gt;adventure&lt;/em&gt;. Mereka start di urutan ke 11 dari 31 &lt;em&gt;challenger&lt;/em&gt; lain. Nisa mengatakan, mobil tak mampu digeber sampai jarak yang ditentukan dan terpaksa dibawa memakai mobil trailer, tetapi panitia menilai mereka sudah masuk finish.&lt;/p&gt;\r\n&lt;p&gt;Ia mengatakan, kendala yang dihadapi adalah tenaga sel surya (&lt;em&gt;solar cell&lt;/em&gt;) yang dipakai tak mampu menyerap sempurna sinar Matahari. Tapi, mobil yang diberinama &lt;em&gt;Sapu Angin Surya&lt;/em&gt; ini tak memalukan. Sebab, ada tim dari negara lain mobilnya sejak awal hingga akhir dinaikkan trailer, misalnya Malaysia. Bahkan ada yang mengundurkan diri.&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;&lt;em&gt;Solar cell&lt;/em&gt; kita kualitasnya belum setara dengan tim lain. Jadi tidak bisa menyerap sinar Matahari. Otomatis tidak bisa mengisi dengan sempurna,&amp;rdquo; katanya. Menurutnya, dalam perhitungan tim awalnya sel surya mampu menyerap sampai 1 kilowatt tapi kenyataannya hanya 600 watt. Mobil bisa digeber maksimal 85 km/jam.&lt;/p&gt;\r\n&lt;p&gt;Ia mengatakan, perbandingan kualitas sel surya miliknya dengan tim lain sangat jauh berbeda. Dari segi harga saja, katanya, tim lain bisa mencapai Rp 10 miliar. &amp;ldquo;Sementara, kita hanya pakai dana sekitar Rp 250 juta,&amp;rdquo; katanya.&lt;/p&gt;\r\n&lt;p&gt;Sebelumnya, ITS pernah ingin mengikuti lomba ini, tapi selalu kandas yaitu melalui mobil Widya Wahanaya I (1985), Widya Wahana II (1990), dan Widya Wahana III (1993). Dan baru tahun ini, mereka lolos dengan mengambil motto &lt;em&gt;Dream Comes True&lt;/em&gt;. Mereka membuat mobil pada Maret-Juni 2013. ITS membiayainya sebesar Rp 1 miliar, Direktorat Jenderal Pendidikan Tinggi sebesar Rp 40 juta, dan sponsor sebanyak Rp 800 juta. &amp;ldquo;Tim sendiri yang mencari sponsor,&amp;rdquo; katanya. Ia mengatakan, pernah melobi ke Pemprov Jawa Timur tapi tidak ada dukungan. Biaya tersebut jauh berbeda dengan negara lain seperti Jepang, Eropa, dan Amerika yang menghabiskan sampai sebesar US$ 9 juta.&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;Bondho Nekad&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Untuk mengikuti lomba ini, boleh dikata mereka hanya mengandalkan biaya minim, mereka hanya memilki tekad alias &amp;ldquo;&lt;em&gt;bondho nekad&lt;/em&gt;&amp;rdquo;. Mereka membayar lomba itu sebesar US$7.000. Nisa mengatakan, biaya transportasi ke Australia juga tak sedikit. Mereka mendapatkan bantuan dari Iuran Orangtua Mahasiswa (Ikoma) ITS sebesar Rp 100 juta. &amp;ldquo;Uang sebesar itu ternyata enggak bisa untuk bolak-balik Surabaya-Australia,&amp;rdquo; katanya. Mereka kemudian mencari sponsor lagi, sehingga terkumpul dana sekitar Rp 200 juta.&lt;/p&gt;\r\n&lt;p&gt;Dana tersebut, katanya, termasuk untuk sewa hotel, makan, persewaan mobil trailer dan mobil pendamping. &amp;ldquo;Panitia tidak memberika fasilitas akomodasi. Kita benar &lt;em&gt;surv&lt;/em&gt;&lt;em&gt;ival&lt;/em&gt;,&amp;rdquo; ujarnya.&lt;/p&gt;\r\n&lt;p&gt;Perjalanan dari Darwin ke Adelaide, mereka harus merasakan perubahan suhu drastis. Dari 40 derajat Celcius, tiba-tiba di daerah Australia Selatan mereka masuk daerah bersuhu 4 derajat Celcius pada malam hari. Selama perjalanan mereka membuat tenda di gurun untuk istirahat pada malam hari, karena mobil tidak berjalan. &amp;ldquo;Alhamdulillah enggak ada yang sakit,&amp;rdquo; katanya.&lt;/p&gt;\r\n&lt;p&gt;Dari tim, mereka menyiapkan dua pengemudi mobil surya. Tim lain bagian dibagi teknisi, mekanikal, logistik, dokumentasi, dan elektrikal. &amp;ldquo;Pas lomba saya navigator,&amp;rdquo; kata mahasiswa Fakultas Teknik Mesin anggkatan 2009 ini. Selama perjalanan, mereka membawa empat mobil pendamping dan mobil&amp;nbsp; trailer, yang seluruhnya sewa. &amp;ldquo;Satu mobil hampir US$ 2.500,&amp;rdquo; katanya.&lt;/p&gt;\r\n&lt;p&gt;Nisa mengatakan, mobil berjalan tidak langsung sampai finish, karena harus berhenti untuk mengisi daya. Dari jarak itu dibagi 9 pos dan mereka berhasil melalui seluruh pos. Mereka sempat mengalami kerusakan aki pada mobil pendamping. &amp;ldquo;Mereka terpaksa ditinggal di belakang, tapi lama kelamaan tidak ada kabarnya,&amp;rdquo; kata Nisa. Akhirnya, satu mobil menjemput kembali, dan ternyata mobilnya mogok. Terpaksa, mereka meminta bantuan panitia.&lt;/p&gt;\r\n&lt;p&gt;Dengan medan yang tak diketahui pasti, mereka merasa was-was tim terpisah. Apalagi kondisinya malam. Mereka khawatir dengan ancaman para Suku Aborigin. &amp;ldquo;Di sana &lt;em&gt;kan&lt;/em&gt; suka nodong. Pernah pas mengisi bensin didatangi suku Aborigin dan minta uang. Kemarin minta satu-dua dolar,&amp;rdquo; tutur Nisa.&lt;/p&gt;\r\n&lt;p&gt;Dalam lomba, mereka harus mengisi daya penuh selama 4 jam, baru kemudian mobil bisa dipakai lagi. Ketika mengisi mobil ditaruh di atas mobil trailer, tapi tidak berhenti. Perjalanan paling hanya sampai 2 jam sekali.&lt;/p&gt;\r\n&lt;p&gt;Ia mengatakan, selama lomba mendapatkan pengalaman banyak. Ia juga berharap dua tahun lagi akan tetap mengikuti lomba demi mengharumkan nama Indonesia. Dari lomba tersebut mereka mendapatkan tropi.&lt;/p&gt;\r\n&lt;p&gt;Ia berharap mobil ini bisa dikembangkan lebih jauh lagi dengan dukungan infrastruktur dari pemerintah, seperti kondisi jalan, sel surya, dan chager di pom bensin. Dengan kesuksesan ini, kata Nisa, sejumlah kampus mulai mengembangkan mobil surya, misalnya Universitas Brawijaya.&lt;/p&gt;', '12', '12', '0', 0, '2013-10-28 16:51:23', '1'),
(105, '2013-10-29', '27', 'Dituntut 17,5 Tahun, Fathanah: Itu Penzaliman, Hanya Cari Sensasi', '2', 'Jakarta', '&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;Dengan penuh emosi, terdakwa Ahmad Fathanah membacakan nota pembelaan pribadinya (pledoi). Fathanah menyebut tuntutan jaksa selama 17 tahun 6 bulan hanya untuk mencari sensasi semata.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;&quot;Itu penzaliman, hanya untuk cari sensasi,&quot; kata Fathanah di Pengadilan Tipikor di Pengadilan Tipikor, Jl HR Rasuna Said, Jakarta Selatan, Senin (28/10/2013) malam.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;Berbatik warna biru, Fathanah membacakan nota pembelaan pribadinya bertajuk ''Hukuman yang Dipaksakan'' sambil berdiri. Sesekali pembacaan harus terhenti karena Fathanah menahan tangis.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;Menurut Fathanah, tuntutan jaksa terhadap dirinya memang sangat luar biasa berat. Ia menyebut tuntutan itu sebagai sesuatu yang emosional.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;&quot;Demikian lamanya, demikian tingginya tuntutan yang diinginkan penuntut umum,&quot; sambung Fathanah.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;Fathanah menceritakan penderitaan yang ia harus alami selama menjalani proses hukum di KPK. Tidak hanya dirinya, tapi seluruh anak, istri hingga keluarga besarnya harus menerima hujatan dari masyarakat.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;&quot;Saya mengalami pukulan moral sosial luar biasa, hujatan, hinaan luar biasa,&quot; kata orang dekat Luthfi Hasan Ishaaq.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;Fathanah dituntut 7 tahun 6 bulan penjara dalam kasus suap impor daging sapi. Sedangkan dalam kasus pencucian uang, jaksa menuntut Fathanah 10 tahun bui.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: medium;&quot;&gt;Fathanah dianggap terbukti melanggar Pasal 12 huruf a UU Pemberantasan Tipikor. Khusus untuk kasus pencucian uang, jaksa menjeratnya dengan Pasal 3 dan Pasal 5 UU No 8/2010.&lt;/span&gt;&lt;/p&gt;', '14', '14', '0', 1, '2013-10-28 14:52:22', '1'),
(106, '2013-10-29', '1', 'ITS Solar Car Racing Team for World Solar Challenge 2013 ', '13', 'JAKARTA (HN) -', '&lt;p&gt;&lt;span style=&quot;font-size: 14pt;&quot;&gt;World Solar Challenge (WSC) 2013 merupakan kompetisi balap mobil bertenaga surya tingkat dunia. Kompetisi ini telah berlangsung sebanyak 11 kali sejak tahun 1987. WSC 2013 merupakan lomba dua tahunan yang diadakan di benua Australia. Jarak tempuh pada lomba ini &amp;plusmn; 3021 km dari Darwin (North teritory) &amp;ndash; Adelaide (South Australia), sebagian besar rute yang dilewati adalah daerah gurun (outback) dan tidak ada fasilitas apapun (tidak ada listrik, air, saluran telepon, dsb-nya). Lomba ini selevel dengan rally Paris-Dakar.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Tahun ini, WSC 2013 diikuti oleh 42 tim dari 24 negara yang terbagi dalam 3 (tiga) kelas antara lain challenger class, cruiser class dan adventure class. ITS Solar Car Racing Team merupakan delegasi Indonesia yang pertama dan satu-satunya dalam WSC ini.&lt;br /&gt;Motto yang dibawa oleh ITS Solar Car Racing Team adalah &amp;ldquo;Dream Comes True&amp;rdquo;. Ini merupakan impian dari tim ITS yang telah terwujud pada tahun ini untuk mengikuti kompetisi solar car tingkat dunia. Mimpi ini sebelumnya pernah diusung dalam mobil Widya Wahana I (1985), Widya Wahana II (1990), dan Widya Wahana III (1993), namun masih belum dapat menembus kompetisi tingkat dunia. Pada tahun inilah ITS Solar Car Racing Team telah berhasil mewujudkan mimpi tersebut untuk berkompetisi dalam WSC 2013 melalui Sapu Angin Surya.&lt;br /&gt;Jum&amp;rsquo;at, 27 September 2013, ITS Solar Car Racing Team telah mendarat di kota Darwin. Tim menuju sirkuit di daerah Hidden Valley. Disana kami memulai proses persiapan pada mobil Sapu Angin Surya, baik dari segi electrical maupun mechanical sebelum nantinya mobil kita nyatakan siap untuk melalui proses scrutineering. &lt;br /&gt;Di World Solar Challenge 2013 ITS Solar Car Racing Team benar benar diuji,disamping menguras semangat, pikiran dan juga finansial. Tim ITS mengikuti kompetisi ini dengan &amp;ldquo;bondho nekat&amp;rdquo;. Dengan dana yang terbilang minim jika dibandingkan dengan tim dari negara lain. ITS Solar Car Racing Team hanya bermodalkan 500 juta rupiah yang berasalkan dari Sponsor dan juga ditambah 1 milyar rupiah dari pemerintah dan ITS. &lt;br /&gt;Akan tetapi, dengan kondisi ini serta dukungan dari seluruh pihak, ITS Solar Car Racing Team selalu berusaha semaksimal mungkin untuk dapat mengharumkan nama bangsa dalam kompetisi ini. Beberapa capaian yang didapatkan tim ITS adalah :&lt;br /&gt;1. Lolos Static dan Dynamic Scrutineering sehingga diperbolehkan race. Capaian ini sangat baik mengingat salah satu tim besar dari Inggris dan Jepang tidak mampu lolos sesi ini sehingga tiddak diperkenankan berkompetisi. &lt;br /&gt;2. Free Practice selama tiga hari di sirkuit Hidden Valley Speedway, Darwin, NT, Australia, tim ITS dapat mengikuti free practice ini dengan sangat baik dan berhasil memperoleh catatan waktu rata-rata 2 menit 24 detik untuk sirkuit sepanjang 2.8 km. Di hari ketiga free practice, saat fast lap tiba-tiba knuckle roda depan patah. Tim memiliki waktu semalam untuk memperbaiki knuckle tersebut. Semangat arek-arek ITS teruji saat ini, bahkan tim tetangga Paddock sari University of Western Sydney sampai geleng-geleng kepala karena tepat pukul 06.00 pagi hari Sabtu, 05 Oktober mobil surya ITS sudah siap lagi mengikuti babak Kualifikasi. &lt;br /&gt;3. Babak kualifikasi, setiap tim diberi kesempatan sekali warming up lap dan sekali timing lap. Seperti di F1, kami harus membaca situasi yang tepat untuk keluar paddock, jam 11 waktu setempat tim memutuskan untuk turun. Warming up berjalan lancar, kemudian dilanjutkan sesi timing lap. Saat itu mobil Surya ITS melaju dengan cepat dan stabil sehingga harus melakukan over take dengan mobil surya Italia (yang saat itu pada sesi warming up), sehingga driver harus mengerem dan mengambil jalur agak ke dalam sehingga keluar dari racing line yang diharapkan. Akhirnya mobil Surya ITS berhasil mencatat waktu 2 menit 26 detik dan berada di posisi 11. Posisi ini lebih baik dari Nuon (pemenang) dan Tokai (runner up) yang saat kualifikasi melintir di tikungan akhir. &lt;br /&gt;4. Race Darwin to Adelaide. Tim ITS start pada urutan 11 dari 31 peserta challenger class pada pukul 09.30 waktu setempat. Tim berhasil menempuh jarak 146 km di hari pertama. Saat dianalisis di performance monitoring system kami ternyata sel surya mengalami kerusakan beberapa modul si bagian kanopi dan side depan mobil surya. Sehingga solar cell hanya bisa membangkitkan daya rata-rata 600 Watt (seharusnya design solar cell dapat membangkitkan 1,177 KW. Kompetisi ini terdiri dari 9 Control Stop, dimana masing-masing control stop memiliki waktu buka dan tutup tertentu. Setiap tim tidak boleh missing control stop 2 secara berurutan. Sehingga tim memiliki strategi untuk menaikkan ke trailer, konsekuensinya jarak solar powered akan berkurang. Alhamdulillah tim ITS berhasil menjalankan mobil surya tiap hari dengan rata-rata menempuh jarak 150 km dengan tenaga matahari. Setelah melewati Allice Spring cuaca berubah menjadi tidak bersahabat, suhu dingin sampai di bawah 10 derajat celcius dengan diikuti angin yang cukup kencang. Tim dari Swedia menjadi korban sehingga mobil mereka terbalik dan mengalami kerusakan, begitu juga dengan tim dari Kanada, Blue sky, Trailer mereka terguling. &lt;br /&gt;5. Finish di Adelaide. Sekitar Pukul 11 mobil surya ITS memasuki parc ferme di finish line kota adelaide dan secara official dinyatakan menempuh jarak 748 km dengan tenaga matahari. Hasil ini patut disyukuri karena sebenarnya tim-tim lain juga harus mengangkut mobil suryanya dengan trailer. Hanya 10 tim saja yang mampu finish tanpa ditrailer. &lt;br /&gt;6. Post Race. Setelah finish di Adelaide tim diberi tempat menginap dan sekaligus dijamu oleh AIDSA (Association of Islamic Dakwah South Australia). Terima kasih kepada kawan-kawan AIDSA khususnya Bapak Basuki Suratno (alumni mesin ITS M24) yang telah menghubungkan kami dengan kawan-kawan AIDSA di Adelaide. Saat ini kami masih menunggu kepastian tiket pulang ke Surabaya-Indonesia.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '27', '27', '0', 0, '2013-10-28 14:29:16', '1'),
(107, '2013-10-29', '3', 'Ini Penampakan Lahan Milik Rudi Rubiandini yang Disita KPK', '2', 'Jakarta', '&lt;p&gt;&lt;span&gt;Komisi Pemberantasan Korupsi (KPK) telah melakukan penyitaan sejumlah rumah dan bangunan milik tersangka kasus suap di lingkungan SKK Migas, Rudi Rubiandini. Salah satu yang disita merupakan lahan yang tidak dihuni atau tanah kosong.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Lahan yang berlokasi di Jalan Haji Ramli No 13 Tebet Dalam, Jakarta Selatan terlihat sepi dan tidak ada penyidik maupun penjaga yang mengawalnya. Di dalamnya terlihat ada puing-puing bangunan yang diduga bekas rumah yang didirikan di atas lahan ini.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Pagar besi setinggi 1,5 meter terlihat membatasi lahan ini dari jalanan. Tidak diketahui persis luas lahan ini, namun untuk ukuran lahan dengan bangunan rumah, lahan ini tergolong kecil.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;&quot;Tanah dan bangunan ini telah disita. Dalam perkara tindak pidana korupsi dengan tersangka Rubi Rubiandini. Ttd Penyidik KPK,&quot; demikian bunyi papan yang menyatakan penyitaan KPK, seperti berdiri di atas tanah kosong tersebut.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Suasana di sekitar lahan yang disita ini tergolong sepi. Kanan dan kiri lahan ini merupakan rumah warga setempat. Jalanan di depan lahan ini cukup sempit dan hanya bisa dilalui satu mobil.&lt;/span&gt;&lt;/p&gt;', '30', NULL, '0', 0, '2013-10-28 14:31:37', '1'),
(108, '2013-10-29', '4', 'test', '3', 'jakarta (HN)', '', '28', '28', '0', 0, '2013-10-28 14:43:44', '1'),
(109, '2013-10-30', '7', 'Pertamina Resmikan Outlet Solar Non Subsidi Keempat Di Merauke', '8', 'JAKARTA (HN) -', '&lt;p&gt;PT Pertamina (Persero) meluncurkan outlet baru Solar Non Subsidi di Merauke. Rencana ini merupakan bagian dari program untuk menekan konsumsi BBM khususnya BBM subsidi jenis solar.&lt;/p&gt;\r\n&lt;p&gt;Konsumsi Solar di wilayah Kabupaten Merauke hingga akhir tahun diperkirakan mencapai 18.500 KL dari kuota 18.000 KL atau over kuota sekitar +2 persen. Saat ini, jumlah kendaraan roda empat di Kabupaten Merauke yang berjumlah 5.365 unit dengan pertumbuhan sebesar 5 persen per tahun, di mana kendaraan angkutan perkebunan merupakan pasar potensial untuk Solar Non Subsidi.&lt;/p&gt;\r\n&lt;p&gt;VP BBM Retail Pertamina Muhammad Iskandar mengatakan pembukaan outlet yang keempat di Papua ini merupakan bagian dari upaya Pertamina dalam menyediakan BBM Non Subsidi di sektor retail yang akan lebih memudahkan masyarakat pengguna BBM Non Subsidi, khususnya di Merauke.&lt;/p&gt;\r\n&lt;p&gt;Pertamina, kata Iskandar, sangat berharap Pemerintah Daerah Kabupaten Merauke dapat bekerjasama dengan Pertamina untuk meningkatkan penjualan BBM Solar Non Subsidi sekaligus mengurangi penggunaan Solar bersubsidi. Adapun, sebagai bentuk pengawasan Pertamina memberikan warna merah pada Solar Non Subsidi sebagai pembeda secara visual dengan Solar bersubsidi.&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;Outlet ini merupakan yang keempat di Papua, di mana dua outlet lainnya berada di Jayapura dan satu outlet di Mimika. Dengan semakin banyaknya outlet Solar Non PSO di Papua, khususnya di Merauke, diharapkan dapat menekan konsumsi Solar bersubsidi di kabupaten ujung Timur Indonesia ini,&amp;rdquo; kata Iskandar dalam keterangan tertulisnya di Jakarta, Selasa (29/10). (Caca Casriwan)&lt;/p&gt;', '11', '18', '0', 1, '2013-10-29 08:53:28', '1'),
