INSERT INTO `tblberita_log` (`idlogberita`, `idBerita`, `halaman`, `tglBerita`, `judulBerita`, `rubrikBerita`, `kota`, `isiBerita`, `penulisBerita`, `editor`, `layouter`, `statusBerita`, `createdAt`, `aktif`) VALUES
(382, 95, '5', '2013-10-25', 'Bahasa Asli', '3', 'Jakarta', '&lt;p&gt;Indonesia memiliki sekitar 700 bahasa asli , namun yang terekam dan terlestarikan dalam bentuk tulisan hanya ada 13 bahasa. &amp;ldquo;Hingga kini baru naskah berbahasa Jawa, Jawa Kuna, dan Bugis yang diusulkan ke UNESCO sebagai dokumen Memory of the World (MOW), padahal dokumen berbahasa Nusantara lain juga sangat potensial,&amp;rdquo; ujar Dr. Ir. Fatimah Zulfah S. Padmadinata, Deputi Bidang Jasa Ilmiah LIPI sekaligus sebagai Ketua Komite Nasional MOW Indonesia di Pusat Data dan Informasi Ilmiah LIPI, Kampus LIPI Gatot Subroto, Jakarta, pada Jum&amp;rsquo;at (18/10) lalu.&lt;/p&gt;\r\n&lt;p&gt;Fatimah menjelaskan, saat ini dokumen-dokumen yang diusulkan untuk MOW antara lain adalah Prasasti Sriwijaya yang berbahasa Melayu Kuna, Hikayat Po&amp;rsquo;cut Mohamad dalam bahasa Aceh, serta Sanghyang Siksakandang Karesian dan Sewaka Darma yang berbahasa Sunda Kuna.&lt;/p&gt;\r\n&lt;p&gt;MOW sendiri merupakan ingatan kolektif manusia berupa warisan atau pusaka dokumenter yang secara sah dapat menjadi bukti kejadian penting dalam sejarah umat manusia. MoW digagas oleh UNESCO pada tahun 1992 dan mempunyai tujuan memfasilitasi preservasi warisan/pusaka dokumenter melalui berbagai cara yang sesuai dengan perkembangan teknologi, meningkatkan kesadaran akan pengamanan dan keberadaan warisan/pusaka dokumenter baik secara individu maupun secara kolektif, dan membantu akses kepada masyarakat baik nasional maupun internasional kepada warisan/pusaka dokumenter bagi pengembangan ilmu pengetahuan di masa yang akan datang&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;Perlu ditunjuk seorang yang ahli dalam bidangnya untuk mengangkat dan kemudian menggarap dokumen tersebut untuk pengusulan sebagai MOW,&amp;rdquo; saran Fatimah.&lt;/p&gt;', '12', NULL, NULL, 0, '2013-10-24 13:07:00', '1'),
(383, 96, '5', '2013-10-25', 'basa', '3', 'jakarta', '&lt;p&gt;Indonesia memiliki sekitar 700 bahasa asli , namun yang terekam dan terlestarikan dalam bentuk tulisan hanya ada 13 bahasa. &amp;ldquo;Hingga kini baru naskah berbahasa Jawa, Jawa Kuna, dan Bugis yang diusulkan ke UNESCO sebagai dokumen Memory of the World (MOW), padahal dokumen berbahasa Nusantara lain juga sangat potensial,&amp;rdquo; ujar Dr. Ir. Fatimah Zulfah S. Padmadinata, Deputi Bidang Jasa Ilmiah LIPI sekaligus sebagai Ketua Komite Nasional MOW Indonesia di Pusat Data dan Informasi Ilmiah LIPI, Kampus LIPI Gatot Subroto, Jakarta, pada Jum&amp;rsquo;at (18/10) lalu.&lt;/p&gt;\r\n&lt;p&gt;Fatimah menjelaskan, saat ini dokumen-dokumen yang diusulkan untuk MOW antara lain adalah Prasasti Sriwijaya yang berbahasa Melayu Kuna, Hikayat Po&amp;rsquo;cut Mohamad dalam bahasa Aceh, serta Sanghyang Siksakandang Karesian dan Sewaka Darma yang berbahasa Sunda Kuna.&lt;/p&gt;\r\n&lt;p&gt;MOW sendiri merupakan ingatan kolektif manusia berupa warisan atau pusaka dokumenter yang secara sah dapat menjadi bukti kejadian penting dalam sejarah umat manusia. MoW digagas oleh UNESCO pada tahun 1992 dan mempunyai tujuan memfasilitasi preservasi warisan/pusaka dokumenter melalui berbagai cara yang sesuai dengan perkembangan teknologi, meningkatkan kesadaran akan pengamanan dan keberadaan warisan/pusaka dokumenter baik secara individu maupun secara kolektif, dan membantu akses kepada masyarakat baik nasional maupun internasional kepada warisan/pusaka dokumenter bagi pengembangan ilmu pengetahuan di masa yang akan datang&lt;/p&gt;\r\n&lt;p&gt;&amp;ldquo;Perlu ditunjuk seorang yang ahli dalam bidangnya untuk mengangkat dan kemudian menggarap dokumen tersebut untuk pengusulan sebagai MOW,&amp;rdquo; saran Fatimah.&lt;/p&gt;', '12', NULL, NULL, 0, '2013-10-24 13:07:25', '1'),
(384, 97, '5', '2013-10-27', 'Selamat datang di unikom', '5', 'Bandung', '&lt;p&gt;&lt;span&gt;UNIKOM Kuliah Online merupakan salah satu sarana pembelajaran interaktif, dimana para dosen dan para mahasiswa dapat berkomunikasi dengan menggunakan media internet.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Para dosen dapat memberikan materi, baik berupa file, video, tulisan ataupun materi-materi kuliah dalam bentuk dokumen lainnya, memberikan informasi-informasi penting kepada para mahasiswanya, membaca dan memberikan jawaban untuk pertanyaan-pertanyaan yang diberikan para mahasiswanya, dan memasukan soal-soal ujian juga menentukan waktu berlakunya ujian untuk para mahasiswanya.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span&gt;Sedangkan untuk para mahasiswa, dapat memilih kelas, dan mata kuliah yang akan diikuti, mahasiswa bisa mendapatkan materi perkuliahan berupa file atau bacaan dari dosen yang bersangkutan, mengirimkan pertanyaan kepada dosen mata kuliah tersebut, mengirimkan kontak pada mahasiswa lain, melihat informasi dari dosen yang bersangkutan, dan melakukan ujian pada waktu yang telah ditetapkan. Mahasiswa juga dapat melihat hasil prestasinya pada modul raport.&lt;/span&gt;&lt;/p&gt;', '1', NULL, NULL, 1, '2013-10-26 03:53:33', '1'),
(385, 98, '5', '2013-10-28', 'Ini Daftar Aset Milik Adiguna yang Dirusak oleh Floren ', '2', 'Jakarta', '&lt;p&gt;Polisi memastikan pelaku perusakan terhadap kediaman Adiguna Sutowo di Pulomas, Jakarta Timur, adalah seorang wanita bernama Floren. Ada barang-barang dan aset milik Adiguna yang rusak. Ini daftarnya.&lt;/p&gt;\r\n&lt;p&gt;Berdasarkan informasi dari Kabid Humas Polda Metro Jaya, Kombes Pol Rikwanto, Floren datang bersama sopir Adiguna, Dariyono. Saat sang sopir membuka pagar, wanita yang digambarkan muda dan cantik ini langsung mengambil alih setir dan merusak semua yang ada di depan rumah.&lt;/p&gt;\r\n&lt;p&gt;&quot;Satpam melihat sopir disuruh turun dan pelaku mengambil alih kemudi dan memundurkan mobil sedan tersebut menabrak Toyota Alphard mengenai pintu sebelah kiri belakang,&quot; jelas Rikwanto dalam pesan singkat kepada detikcom, Minggu (27/10/2013).&lt;/p&gt;\r\n&lt;p&gt;Kemudian, Floren menancap gas dan menabrak pintu gerbang dan pintu garasi yang di dalamnya ada mobil Mercy bernopol B 171 VAS. Saking kencangnya, Floren juga menabrak pintu dapur hingga rusak.&lt;/p&gt;\r\n&lt;p&gt;Setelah itu, mobil Floren mundur dan merusak gerbang gudang. Karena mobilnya langsung mati, Floren langsung masuk ke garasi dan membanting TV 21 inchi.&lt;/p&gt;\r\n&lt;p&gt;&quot;Pelaku mengambil payung dari mobil Mercy korban dan oleh pelaku payung tersebut digunakan untuk memukul mobil LEXUS B 171 AV,&quot; sambungnya.&lt;/p&gt;\r\n&lt;p&gt;Dengan demikian, aset yang rusak di rumah Adiguna adalah:&lt;/p&gt;\r\n&lt;ol&gt;\r\n&lt;li&gt;Pagar rumah&lt;/li&gt;\r\n&lt;li&gt;Mobil Mercy&lt;/li&gt;\r\n&lt;li&gt;Mobil Lexus&lt;/li&gt;\r\n&lt;li&gt;Mobil Alphard&lt;/li&gt;\r\n&lt;li&gt;Pintu gudang&lt;/li&gt;\r\n&lt;li&gt;TV 21 inchi&lt;/li&gt;\r\n&lt;/ol&gt;\r\n&lt;p&gt;&lt;br /&gt;Pengacara istri Adiguna sebelumnya menyebut total kerugian diprediksi sekitar ratusan juta rupiah.&lt;/p&gt;', '1', NULL, NULL, 1, '2013-10-27 10:09:54', '1'),
(386, 99, '3', '2013-10-28', '523 Pelaku Olahraga Dapat Penghargaan Pemerintah', '5', 'Bandung', '&lt;p&gt;tes ajaj&lt;/p&gt;', '1', NULL, NULL, 1, '2013-10-27 10:16:47', '1'),
(387, 99, '3', '2013-10-28', '523 Pelaku Olahraga Dapat Penghargaan Pemerintah', '5', 'Bandung', '&lt;p&gt;Floren menabrak pagar rumah dan merusak sejumlah properti milik Adiguna Sutowo di Pulogadung, Jakarta Timur. Tak hanya itu, dia juga teriak-teriak sambil mencari istri kedua Adiguna, Vika Dewayani.&lt;/p&gt;\r\n&lt;p&gt;Kabid Humas Polda Metro Jaya Kombes Pol Rikwanto mengatakan, Floren berteriak tak lama setelah menabrakkan mobil Mercy ke pagar. Dia mencari Vika, yang saat itu sedang berada di Bali.&lt;/p&gt;', '1', '1', NULL, 1, '2013-10-27 11:53:30', '1'),
(388, 99, '3', '2013-10-28', '523 Pelaku Olahraga Dapat Penghargaan Pemerintah', '5', 'Bandung', '&lt;p&gt;Floren menabrak pagar rumah dan merusak sejumlah properti milik Adiguna Sutowo di Pulogadung, Jakarta Timur. Tak hanya itu, dia juga teriak-teriak sambil mencari istri kedua Adiguna, Vika Dewayani.&lt;/p&gt;\r\n&lt;p&gt;Kabid Humas Polda Metro Jaya Kombes Pol Rikwanto mengatakan, Floren berteriak tak lama setelah menabrakkan mobil Mercy ke pagar. Dia mencari Vika, yang saat itu sedang berada di Bali.&lt;/p&gt;', '1', '1', NULL, 3, '2013-10-27 11:53:37', '1'),
